(1, 'Doc Manager', '<strong>1.1</strong> Quickly perform bulk updates to the Documents in your site including templates, publishing details, and permissions', 0, 0, 4, 0, 0, '', 0, '', 0, 0, 'docman435243542tf542t5t', 1, '', 'include_once(MODX_BASE_PATH.''assets/modules/docmanager/classes/docmanager.class.php'');\ninclude_once(MODX_BASE_PATH.''assets/modules/docmanager/classes/dm_frontend.class.php'');\ninclude_once(MODX_BASE_PATH.''assets/modules/docmanager/classes/dm_backend.class.php'');\n\n$dm = new DocManager($modx);\n$dmf = new DocManagerFrontend($dm, $modx);\n$dmb = new DocManagerBackend($dm, $modx);\n\n$dm->ph = $dm->getLang();\n$dm->ph[''theme''] = $dm->getTheme();\n$dm->ph[''ajax.endpoint''] = MODX_SITE_URL.''assets/modules/docmanager/tv.ajax.php'';\n$dm->ph[''datepicker.offset''] = $modx->config[''datepicker_offset''];\n$dm->ph[''datetime.format''] = $modx->config[''datetime_format''];\n\nif (isset($_POST[''tabAction''])) {\n    $dmb->handlePostback();\n} else {\n    $dmf->getViews();\n    echo $dm->parseTemplate(''main.tpl'', $dm->ph);\n}'),
(2, 'tour', '', 0, 0, 0, 0, 0, '', 0, '', 0, 0, '', 0, '', '$basePath=$modx->config[''rb_base_dir''];// путь к сайту\r\n//error_reporting(E_ERROR);\r\n	$get            = isset($_GET[''get'']) ? $_GET[''get''] : "redirect";\r\n	$res            = Array();\r\n	$res[''version''] = "v 1.0";\r\n	$res[''url'']     = $table[''url''] = $url = "index.php?a=112&id=".$_GET[''id''];\r\n$res[''get'']     = isset($_GET[''get'']) ? $_GET[''get''] : "";\r\n\r\nswitch ($get) {\r\n\r\n		\r\n	//add tour to base		\r\n	case ''add'':\r\n		//read all kind of tour\r\n		$a = $modx->getDocumentChildren(7);\r\n		foreach($a as $kind) {\r\n			$alltype["id"][] = $kind[''id''];\r\n			$alltype[''title''][] = $kind[''pagetitle''];\r\n		}\r\n		if(!empty($_GET[''item_id''])){//редактирование записи\r\n			$data_query = $modx->db->query("SELECT id_type \r\n												FROM modx_a_alantur_orders_type \r\n												WHERE \r\n													id_tour=''".$modx->db->escape($_GET[''item_id''])."''"\r\n										  );	\r\n			while ($data = $modx->db->getRow($data_query)){\r\n				$type[] = $data[''id_type''];\r\n			}\r\n			$data = "SELECT title, showing, description, country, price \r\n						FROM modx_a_alantur_tours \r\n						WHERE \r\n							id=''".$modx->db->escape($_GET[''item_id''])."''";\r\n			$data = $modx->db->query($data);\r\n			$data = $modx->db->getRow($data);			\r\n			$_GET[''item_id''] = (int)($_GET[''item_id'']);\r\n			$path = MODX_BASE_PATH.''assets/images/''.$_GET[''item_id'']."/*";\r\n			$folder = glob($path);\r\n			\r\n			foreach($folder as $file){\r\n				$filename[] = substr($file, 29);	\r\n			}\r\n			$class = "update";\r\n		}else{//если запись новая\r\n			$class="save";		\r\n		}\r\n		\r\n		if($data[''showing'']){\r\n			$showing="checked";\r\n		}else{\r\n			$showing="";\r\n		}\r\n		$tpl=MODX_BASE_PATH. "assets/modules/goods/Editform.tpl";\r\n	break;\r\n		\r\n		\r\n		\r\n	//Обновление записи в БД\r\n	case ''update'':\r\n		//convert value of checkpoint "showing" to number for save in database\r\n		// "1" - it was checked, "0" - didn''t choice\r\n		if ($_POST[''showing'']) $_POST[''showing''] = 1;\r\n		else $_POST[''showing''] = 0;\r\n		//convert checkpoints of kind of tours to number for save in database\r\n		$modx->db->query("DELETE \r\n							FROM `modx_a_alantur_orders_type`\r\n							WHERE\r\n								id_tour = ''".$modx->db->escape($_POST[''item_id''])."''"\r\n						);\r\n		foreach($_POST["type"] as $key=>$type)\r\n			if($type){\r\n				$value[]=$key;\r\n				$modx->db->query("INSERT INTO modx_a_alantur_orders_type\r\n											SET \r\n												id_tour=''".$_POST[''item_id'']."'',\r\n												id_type=''".$key."''"\r\n								);\r\n			}\r\n		$title = translit($_POST[''title'']);\r\n		$fields = array(\r\n			"title" => $_POST[''title''],\r\n			"description" => $_POST[''description''],\r\n			"country" => $_POST[''country''],\r\n			"showing" => $_POST[''showing''],\r\n			"price" => $_POST[''price''],\r\n			"path" => $title\r\n		);\r\n		if(!empty($_FILES[''cover''][''name''])){\r\n			$folder = $_POST[''item_id''];\r\n			chdir ("../assets/images/".$_POST[''item_id'']);\r\n			$ext = substr($_FILES[''cover''][''name''],-strpos(strrev($_FILES[''cover''][''name'']),''.''));\r\n			$number = count(glob("*.*"))+1;\r\n			if($number==1)$fields[''cover''] = $_POST[''item_id'']."1";\r\n			include MODX_BASE_PATH."assets/modules/goods/downloadimage.tpl";\r\n		}else{\r\n			echo "empty";\r\n		}\r\n			\r\n			\r\n			\r\n		$query = $modx->db->update($fields,"modx_a_alantur_tours","id = ".$_POST[''item_id'']."");\r\n	header("Location:$url");\r\n	break;\r\n	\r\n		\r\n	//сохранения в базе данных\r\n	case ''save'':\r\n			if ($_POST[''showing'']) {$_POST[''showing'']="1";}else{$_POST[''showing'']="0";};\r\n			$title = translit($modx->db->escape($_POST[''title'']));							\r\n				$data=$modx->db->query("INSERT INTO `modx_a_alantur_tours` SET \r\n											title=''".$modx->db->escape($_POST[''title''])."'',\r\n											description=''".$modx->db->escape($_POST[''description''])."'',\r\n											country=''".(int)$_POST[''country'']."'',\r\n											showing= ''".(int)$_POST[''showing'']."'',\r\n											price= ''".(int)$_POST[''price'']."'',\r\n											path=''".$title."''");\r\n			$folder=$modx->db->getInsertId($data);\r\n			if($_POST["type"] ){\r\n				foreach($_POST["type"] as $key=>$type){\r\n						$value[]=$key;\r\n						$modx->db->query("INSERT INTO modx_a_alantur_orders_type SET \r\n											id_tour=''".$folder."'',\r\n											id_type=''".$key."''");\r\n				}\r\n			}\r\n			mkdir("../assets/images/".$folder,0777);\r\n			if(!empty($_FILES[''cover''][''name''])){\r\n				chdir ("../assets/images/".$folder);\r\n				$ext = substr($_FILES[''cover''][''name''],-strpos(strrev($_FILES[''cover''][''name'']),''.''));\r\n				$number = count(glob("*.*"))+1;\r\n					if($number == 1)$fields[''cover''] = $folder."1";\r\n				\r\n				$modx->db->query("UPDATE `modx_a_alantur_tours`  SET cover = ''".$folder.$number."'' WHERE id = ''".$folder."''");\r\n				$tpl = MODX_BASE_PATH."assets/modules/goods/downloadimage.tpl";\r\n				\r\n			}else{\r\n				echo "empty";\r\n			}\r\n			\r\n				header("Location:$url");\r\n	break;	\r\n			\r\n		\r\n		//Перезагрузка страницы (сброс $_GET)\r\n	case ''reload'':\r\n		header("Location:$url");\r\n	break;\r\n		\r\n	//Удаление записи с БД\r\n	case ''delete'':\r\n		$_GET[''item_id''] = (int)$_GET[''item_id''];\r\n		$path = MODX_BASE_PATH.''assets/images/''.$_GET[''item_id''];\r\n		$folder= glob($path);\r\n		if ($folder = glob($path."/*")) {\r\n			foreach($folder as $obj) {\r\n		 		is_dir($obj) ? removeDirectory($obj) : unlink($obj);\r\n			}\r\n		}\r\n		rmdir($path);\r\n		$modx->db->query("DELETE\r\n							FROM `modx_a_alantur_tours`\r\n							WHERE\r\n								id = ''".(int)$_GET[''item_id'']."''"\r\n						);\r\n			header("Location:$url");\r\n	break;\r\n		\r\n		\r\n	case ''DeleteImage'':\r\n		$_GET[''filename''] = $modx->db->escape($_GET[''filename'']);\r\n		$error = unlink(MODX_BASE_PATH.$_GET[''filename'']);\r\n		if