iles."/*";\r\n			$folder = glob($path);\r\n			$firstImage[] = substr($folder[''0''], 29);\r\n		}\r\n		$count_goods = count($id);	\r\n		$tpl= $basePath."modules/goods/table.tpl";	\r\n	break;\r\n}\r\nob_start();\r\ninclude $tpl;\r\n$res[''content''] = ob_get_contents();\r\nob_end_clean();\r\ninclude MODX_BASE_PATH."assets/modules/goods/html.tpl";\r\ndie();\r\n\r\n\r\n\r\nfunction translit($str) {\r\n	$rus = array(''А'', ''Б'', ''В'', ''Г'', ''Д'', ''Е'', ''Ё'', ''Ж'', ''З'', ''И'', ''Й'', ''К'', ''Л'', ''М'', ''Н'', ''О'', ''П'', ''Р'', ''С'', ''Т'', ''У'', ''Ф'', ''Х'', ''Ц'', ''Ч'', ''Ш'', ''Щ'', ''Ъ'', ''Ы'', ''Ь'', ''Э'', ''Ю'', ''Я'', ''а'', ''б'', ''в'', ''г'', ''д'', ''е'', ''ё'', ''ж'', ''з'', ''и'', ''й'', ''к'', ''л'', ''м'', ''н'', ''о'', ''п'', ''р'', ''с'', ''т'', ''у'', ''ф'', ''х'', ''ц'', ''ч'', ''ш'', ''щ'', ''ъ'', ''ы'', ''ь'', ''э'', ''ю'', ''я'','' '');\r\n	$lat = array(''a'', ''b'', ''v'', ''g'', ''d'', ''e'', ''e'', ''gh'', ''z'', ''i'', ''y'', ''k'', ''l'', ''m'', ''n'', ''o'', ''p'', ''r'', ''s'', ''t'', ''u'', ''f'', ''h'', ''c'', ''ch'', ''sh'', ''sch'', ''y'', ''y'', ''y'', ''e'', ''yu'', ''ya'', ''a'', ''b'', ''v'', ''g'', ''d'', ''e'', ''e'', ''gh'', ''z'', ''i'', ''y'', ''k'', ''l'', ''m'', ''n'', ''o'', ''p'', ''r'', ''s'', ''t'', ''u'', ''f'', ''h'', ''c'', ''ch'', ''sh'', ''sch'', ''y'', ''y'', ''y'', ''e'', ''yu'', ''ya'', "-");\r\n return str_replace($rus, $lat, $str);\r\n}\r\n		\r\n		\r\n		\r\n\r\n\r\n	\r\n'),
(3, 'reports', '', 0, 0, 0, 0, 0, '', 0, '', 0, 0, '06270b53d0724df7dfb713f43df8acb8', 0, '', '$basePath = $modx->config[''rb_base_dir''];// путь к сайту\r\n	error_reporting(E_ERROR);\r\n	$get            = isset($_GET[''get'']) ? $_GET[''get''] : "redirect";\r\n	$res            = Array();\r\n	$res[''version''] = "v 1.0";\r\n	$res[''url'']     = $table[''url''] = $url = "index.php?a=112&id=".$_GET[''id''];\r\n$res[''get'']     = isset($_GET[''get'']) ? $_GET[''get''] : "";\r\n\r\nswitch ($get) {\r\n\r\n	case ''add'':\r\n		if(!empty($_GET[''id_order''])){//редактирование записи\r\n			$_GET[''id_order''] = $modx->db->escape($_GET[''id_order'']);	\r\n			$sql="SELECT * \r\n					FROM modx_a_alantur_orders_detail\r\n					WHERE \r\n						id_order = ''".$_GET[''id_order'']."''";\r\n			$data_query = $modx->db->query($sql);		\r\n			while ($data = $modx->db->getRow($data_query)){\r\n					$id_photo[] = $data[''id_photo''];\r\n					$photo_name[] = $data[''photo_name''];\r\n					$description[] = $data[''description''];\r\n			};\r\n			$class = "update";	\r\n			$tpl = $basePath."modules/report/editreport.tpl";\r\n		}\r\n	break;\r\n					\r\n		//Перезагрузка страницы (сброс $_GET)\r\n	case ''reload'':\r\n		header("Location:$url");\r\n	break;\r\n		\r\n\r\n	// обновление статуса отчета (опубликовано, отклонено или непросмотрено)\r\n	case ''public'':\r\n		$sql="UPDATE modx_a_alantur_orders\r\n					SET \r\n						showing = ''".$modx->db->escape($_GET[''result''])."'' \r\n						WHERE id_order = ''".$modx->db->escape($_GET[''id_order''])."''";\r\n		$modx->db->query($sql);\r\n		header("Location:$url");\r\n	break;\r\n					\r\n		\r\n	//Страница модуля модерации отчетов\r\n	default:	\r\n		$data_query="SELECT * \r\n						FROM modx_a_alantur_orders\r\n						WHERE \r\n							showing<>0 \r\n						ORDER BY id_order ASC";\r\n	/*	$tours = $modx->db->makeArray($modx->db->query("SELECT *,\r\n															(SELECT username\r\n																FROM modx_web_users\r\n																WHERE\r\n															