(4, 'orders', '', 0, 0, 0, 0, 0, '', 0, '', 0, 0, 'eb94b06ae29fc0efff3a2fe252a85d37', 0, '', '$basePath=$modx->config[''rb_base_dir''];// путь к сайту\r\nerror_reporting(E_ERROR);\r\n$get            = isset($_GET[''get'']) ? $_GET[''get''] : "redirect";\r\n$res            = Array();\r\n$res[''version''] = "v 1.0";\r\n$res[''url'']     = $table[''url''] = $url = "index.php?a=112&id=".$_GET[''id''];\r\n$res[''get'']     = isset($_GET[''get'']) ? $_GET[''get''] : "";\r\n\r\n//	\r\n\r\nswitch ($get) {\r\n	\r\n	\r\n	//	Обновляем данные пользователя по кнопке \r\n	case ''update'':\r\n	foreach($_POST[''status''] as $key=>$value){\r\n		$modx->db->query("UPDATE `modx_a_alantur_tours_sale`\r\n								SET status=''".$modx->db->escape($value)."'' \r\n								WHERE \r\n									id =''".$modx->db->escape($key)."''"\r\n						);\r\n	}	\r\n	header("Location:$url");\r\n	break;\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	//  обновляем данные о статусе заказа с помощью ajax\r\n	case ''sendtour'':\r\n		$modx->db->query("UPDATE `modx_a_alantur_tours_sale`\r\n									SET\r\n										status=''".(int)$_POST[''status'']."'' \r\n									WHERE \r\n										id =''".(int)$_POST[''tour'']."''"\r\n						);\r\n		\r\n		$id_name = $modx->db->getRow($modx->db->query("SELECT user_id \r\n															FROM `modx_a_alantur_tours_sale`\r\n															WHERE \r\n																id=''".(int)$_POST[''tour'']."''"\r\n													 )\r\n									);\r\n		$name = $modx->db->getRow($modx->db->query("SELECT username \r\n															FROM `modx_web_users`\r\n															WHERE \r\n																id=''".$id_name[''user_id'']."''"\r\n												  )\r\n								 );\r\n		$contact = $modx->db->getRow($modx->db->query("SELECT phone,email \r\n															FROM `modx_web_user_attributes` \r\n															WHERE\r\n																internalKey	=''".$id_name[''user_id'']."''"\r\n													 )\r\n									);\r\n		$phone = "(057) 700-00-00";\r\n		$from = " менеджеров турагенства alantur.loc";\r\n		$email = $contact[''email''];\r\n		$status = status((int)$_POST[''status'']);\r\n		$message .= "Добрый день,".$name[''username'']." изменился статус Вашего заказа №".(int)$_POST[''tour'']." на сайте Alantur.loc. Новый статус ''".$status."''";\r\n		$tpl = "email_letter";\r\n		$tpl_letter = $modx->parseChunk($tpl,array("name"=>$from,"phone"=>$phone,"message"=>$message));\r\n		$modx->runSnippet("Snippet", Array("to" => $email,\r\n							  				"get"=>"sendmail",\r\n							  				"subject"=>"Письмо об изменении статуса заказа на сайте Alantur.loc",\r\n							  				"body"=>$tpl_letter\r\n						  					)\r\n						 );\r\n		die;\r\n	break;\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	//Страница модуля заказа туров\r\n	default:	\r\n		$sales = $modx->db->makeArray($modx->db->query("\r\n														SELECT * ,\r\n															(SELECT username \r\n																FROM `modx_web_users`\r\n																WHERE \r\n																	id= t.user_id) \r\n																AS ''name'',\r\n															(SELECT title \r\n																FROM `modx_a_alantur_tours`\r\n																WHERE \r\n																	id= t.tour_id) \r\n																AS ''title''\r\n															FROM `modx_a_alantur_tours_sale` t\r\n															ORDER BY t.id ASC"\r\n													  )\r\n									 );\r\n\r\n		\r\n		$a = count($sales);\r\n		$tpl = $basePath."modules/sale/table.tpl";			\r\n	break;\r\n}\r\n\r\nob_start();\r\ninclude $tpl;\r\n$res[''content''] = ob_get_contents();\r\nob_end_clean();\r\n\r\ninclude $basePath."modules/sale/html.tpl";\r\n\r\ndie;\r\n\r\nfunction status($a){\r\n	switch ($a){\r\n		case 1:\r\n		$status = "Новый заказ тура";\r\n		break;\r\n		case 2:\r\n		$status = "Заказ тура находится в обработке";\r\n		break;\r\n		case 3:\r\n		$status = "Заказчику отослан счет, ждем оплаты";\r\n		break;\r\n		case 4:\r\n		$status = "Заказ тура отменен";\r\n		break;\r\n		default:\r\n		$status = "Ошибка";\r\n		break;\r\n	}\r\n	return $status;\r\n}\r\n\r\n\r\n\r\n\r\n\r\n'),
